import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as THREE from "three";

class App extends Component {
  componentDidMount() {
    var scene = new THREE.Scene();
    var camera = new THREE.PerspectiveCamera(
      90,
      window.innerWidth / window.innerHeight,
      1,
      1000
    );

    var renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    this.mount.appendChild(renderer.domElement);

    var geometry = new THREE.BoxGeometry(8, 8, 8,50,50,50);
    var material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
    var cube = new THREE.Mesh(geometry, material);
    var bGeometry = new THREE.BufferGeometry();
    bGeometry.fromGeometry(geometry);

    var geometry2 = new THREE.CylinderGeometry( 5, 5, 5, 50,50,50 );
    var material2 = new THREE.MeshBasicMaterial( {color: 0xffff00} );
    var cylinder = new THREE.Mesh( geometry2, material2 );

    scene.add(cube);
    scene.add(cylinder);
    var a = new THREE.Vector3(0, 0, 0);
    const X=12;
    const Y=10;
    const Z=10;
    cube.position.x = X;
    cube.position.y = Y;
    cube.position.z = Z;
    camera.lookAt(a)
    var car=[];
    var positions = bGeometry.getAttribute('position');
   for(let k=0;k<positions.count;k++){
    var myPosition =  new THREE.Vector3(positions.array[k*3]+X, positions.array[k*3 + 1]+Y, positions.array[k*3 + 2]+Z,);
    car[k]=myPosition;    
    console.log(car[k]);
    }
    var animate = function() {
      requestAnimationFrame(animate);
      //cube.rotation.x += 0.01;
      //cube.rotation.y += 0.01;
      //console.log(bGeometry.getAttribute('position'));
     renderer.render(scene, camera);
    };

    animate();

  }
  render() {
    return <div ref={ref => (this.mount = ref)} />;
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
